
$(document).ready(function(){


    $("#clientMessageForm").validate({
        rules: {
            'data[Client][name]': {
                required: true
            },
            'data[Client][email]': {
                required: true,
                email: true
            },
			'data[Client][contact]': {
                required: true,
                number: true
            },
			'data[Client][message]': {
                required: true,
            },
			'hiddenRecaptcha': {
			    
				required: function() {
					 if(grecaptcha.getResponse() == '') {
						
						 $('#hiddenRecaptcha-error').show();
						 return true;
					 } else {
						
						$('#hiddenRecaptcha-error').hide();
						return false;
					 }
				}
		    }
           
        },
        messages: {
            'data[Client][name]': {
                required: "Please enter name."
            },
            'data[Client][email]':{
                required:"Please enter email."
            },
			'data[Client][contact]':{
                required:"Please enter contact number."
            },
			'data[Client][message]':{
                required:"Please enter message."
            },
			'hiddenRecaptcha':{
                required:"Please verify that you are not a robot."
            }
        }
    });
	


});

